package com.fitness.workout.data.repository

import org.junit.Assert.assertEquals
import org.junit.Test

class WorkoutSeedDataTest {

    @Test
    fun `durations sum equals category meta for known title`() {
        val title = "Glute Activation Warmup"
        val meta = WorkoutSeedData.categoryMeta["Booty"]!!
        val expectedTotal = meta.durations[meta.titles.indexOf(title)]
        val count = meta.counts[meta.titles.indexOf(title)]

        val exercises = WorkoutSeedData.generateExercisesForWorkout(1, title, count, "Booty")
        val sum = exercises.sumOf { it.duration }

        assertEquals("Sum of exercise durations should equal category meta duration", expectedTotal, sum)
    }

    @Test
    fun `no meta uses base durations`() {
        val title = "Some Unknown Workout"
        val count = 4
        val exercises = WorkoutSeedData.generateExercisesForWorkout(2, title, count, "")
        // When there's no meta, behavior is to use template base durations; ensure we returned requested count
        assertEquals("Should return requested exercise count", count, exercises.size)
    }
}
